public class ForTest02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum = 0;
		
		for(int i=0; i<3; i++) {
			sum += i;
		}
		System.out.println("sum: "+sum);
		
	}

}
